  date: '2022-07-03'
}];
export const paymentState = atom({
  key: 'paymentState',
  default: payments.map((payment, index) => ({
    id: index,
    cost: payment.cost,
    name: payment.name,
    category: payment.category,
    payedBy: payment.payedBy,
    date: payment.date,
    point: payment.payedBy === 'credit' ? payment.const * 0.1 : 0,
    onEdit: false
  }))
});
export const defaultPayment = atom({
  key: 'defaultPayment',
  default: {
    id: null,
    cost: 0,
    name: '',
    category: '',
    payedBy: 'credit',
    date: '',
    point: 0,
    onEdit: false
  }
});
