import { useRecoilValue } from 'recoil'
import { Grid, Paper } from '@mui/material'

//表示データ（お支払いデータ）・月、日ごと抜粋
import { paymentState } from '../../recoil/atoms'
import CrudUnitTable from '../organisms/crudUnitTable'

//スライドショー
import 'swiper/css'
import 'swiper/css/pagination'
import "swiper/css/navigation"
import { Swiper, SwiperSlide } from 'swiper/react'
import { Pagination, Navigation } from 'swiper'

//カレンダー（FullCalendar）
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid';

function CreatePaymentsTemplate () {
    const payments = useRecoilValue(paymentState)

    var start = new Date("2022-07-01")
    var end = new Date("2022-07-31")
    const monthlyDays = [...Array(31)].map((_, index: number)=>{
        if (index < 10) { return "2022-07-0"+ (index+1) }
        if (index >= 10) { return "2022-07-"+ (index+1) }
    })

    return(
    <div>
        <h1>新規作成</h1>
        <Grid container spacing={2}>
            <Grid item xs={6}>
                <Paper style={{padding: "20px", margin: "10px"}}>
                    <FullCalendar
                        plugins={[dayGridPlugin, timeGridPlugin]}
                        initialView="dayGridMonth"
                        events={[
                            {title:'event1', start: '2022-07-14'},
                            {title:'event2', start: '2022-07-15', end: '2022-07-16'}
                        ]}
                    />
                </Paper>
            </Grid>
            <Grid item xs={6}>

            </Grid>
        </Grid>
    </div>
    )
}

export default CreatePaymentsTemplate
