import { Button, ButtonGroup } from "@mui/material"

import CreditCardIcon from '@mui/icons-material/CreditCard'
import MonetizationOnIcon from '@mui/icons-material/MonetizationOn'

import { useRecoilValue, useRecoilState } from 'recoil'
import { paymentState } from '../../recoil/atoms'

const choosedStyle = {
    color: "#474747", 
    background: "#fffa0f",
}

const notChoosedStyle = {
    color: "#d6d6d6",
    background: "#e8e8e8",
}

function ChoosePayedByButton(props: any) {

    return(
        <div>
            <ButtonGroup>
                <Button 
                    sx={ props.payment.payedBy === 'credit' ? choosedStyle : notChoosedStyle }
                    onClick = {props.handleClickCredit(props.payment)}
                >
                    <CreditCardIcon />
                </Button>
                <Button
                    sx={ props.payment.payedBy === 'cash' ? choosedStyle : notChoosedStyle }
                    onClick = {props.handleClickCash(props.payment)}
                >
                    <MonetizationOnIcon />
                </Button>
            </ButtonGroup>   
        </div>
    )
}

export default ChoosePayedByButton
