import { atom } from 'recoil';
const payments = [{
  cost: 1000,
  name: '買い物',
  category: '食費',
  payedBy: 'credit',
  date: '2022-08-01'
}, {
  cost: 2000,
  name: '買い物',
  category: '食費',
  payedBy: 'credit',
  date: '2022-08-02'
}, {
  cost: 3000,
  name: '買い物',
  category: '生活費',
  payedBy: 'cash',
  date: '2022-08-03'
}];
export const paymentState = atom({
  key: 'paymentState',
  default: payments.map((payment, index) => ({
    id: index,
    cost: payment.cost,
    name: payment.name,
    category: payment.category,
    payedBy: payment.payedBy,
    date: payment.date,
    point: payment.payedBy === 'credit' ? payment.const * 0.1 : 0,
    onEdit: false
  }))
});
export const defaultPayment = {
  id: null,
  cost: 0,
  name: '',
  category: '',
  payedBy: 'credit',
  date: '',
  point: 0,
  onEdit: false
};
