//propsとして、コンポーネント配列の１要素（コンポーネント）を受け取る

import { Button, ButtonGroup } from "@mui/material"

//２択ボタンのアイコンをインストール
import CreditCardIcon from '@mui/icons-material/CreditCard'
import MonetizationOnIcon from '@mui/icons-material/MonetizationOn'

//変更するコンポーネント配列をインポート
import { useRecoilState } from 'recoil'
import { paymentState } from '../../../recoil/atoms'

//ボタンの選択・未選択時のデザイン
const choosedStyle = {
    color: "#474747", 
    background: "#fffa0f",
}
const notChoosedStyle = {
    color: "#d6d6d6",
    background: "#e8e8e8",
}

function ChoosePayedByButton(props: any) {

    //変更するコンポーネント配列の全体を読み込む
    const [payments, setPayments] = useRecoilState(paymentState)

    //両方のボタンに値の変更関数を設定
    const handleClickCredit = (payment: any) => {
        setPayments([
            ...payments.slice(0, payment.id),
            {...payment, payedBy: 'credit'},
            ...payments.slice(payment.id+1)
        ])
    }
    const handleClickCash = (payment: any) => {
        setPayments([
            ...payments.slice(0, payment.id),
            {...payment, payedBy: 'cash'},
            ...payments.slice(payment.id+1)
        ])
    }

    return(
        <ButtonGroup>
            <Button 
                sx={ props.payment.payedBy === 'credit' ? choosedStyle : notChoosedStyle }
                onClick={()=>handleClickCredit(props.payment)}
            >
                <CreditCardIcon />
            </Button>
            <Button
                sx={ props.payment.payedBy === 'cash' ? choosedStyle : notChoosedStyle }
                onClick={()=>handleClickCash(props.payment)}
            >
                <MonetizationOnIcon />
            </Button>
        </ButtonGroup>   
    )
}

export default ChoosePayedByButton
