import { TableCell, TextField } from '@mui/material'
import { useState } from 'react'

//変更するコンポーネント配列をインポート
import { useRecoilState, useRecoilValue } from 'recoil'
import { paymentState } from '../../../recoil/atoms'

function ValueEditTextField(props: any) {
    const payment = props.payment
    const editElement = props.editElement

    const keys = Object.keys(payment)
    const editElementKey = keys.find((key) => key === editElement)

    const [payments, setPayments] = useRecoilState(paymentState)

    //テキストフィールドで編集する要素分の条件分岐を用意
    const handleChange = (event: any) => {
        if(editElementKey === 'cost'){
            setPayments([
                ...payments.slice(0, payment.id),
                {...payment, cost: event?.target.value},
                ...payments.slice(payment.id+1)
            ])
        }
        if(editElementKey === 'name'){
            setPayments([
                ...payments.slice(0, payment.id),
                {...payment, name: event?.target.value},
                ...payments.slice(payment.id+1)
            ])
        }
        if(editElementKey === 'category'){
            setPayments([
                ...payments.slice(0, payment.id),
                {...payment, cost: event?.target.value},
                ...payments.slice(payment.id+1)
            ])
        }
        if(editElementKey === 'date'){
            setPayments([
                ...payments.slice(0, payment.id),
                {...payment, cost: event?.target.value},
                ...payments.slice(payment.id+1)
            ])
        }
    }

    const [textFieldValue, setTextFieldValue] = useState()
    if(editElementKey === 'cost'){
        setTextFieldValue(payment.cost)
    }
    if(editElementKey === 'name'){
        setTextFieldValue(payment.name)
    }
    if(editElementKey === 'category'){
        setTextFieldValue(payment.category)
    }
    if(editElementKey === 'date'){
        setTextFieldValue(payment.date)
    }

    return (
        <TextField 
            value={textFieldValue}
            onChange={handleChange} 
        />
    )
}

export default ValueEditTextField
