//textEditField.tsx

import { TextField } from '@mui/material'

//変更するコンポーネント配列をインポート（元もあるrow＋新しいrow）
import { useState } from 'react'
import { useRecoilState } from 'recoil'
import { paymentState } from '../../../recoil/atoms'
import { defaultPayment } from '../../../recoil/atoms'

//
import { editPayment } from '../../../recoil/atoms'

function ValueEditTextField(props: any) {
    const payment = props.payment
    const editElement = props.editElement

    const keys = Object.keys(payment)
    const editElementKey = keys.find((key) => key === editElement)

    //const [payments, setPayments] = useRecoilState(paymentState)
    const [ newPayment, setNewPayment] = useRecoilState(defaultPayment)
    const [ paymentForEdit, setPaymentForEdit ] = useRecoilState(editPayment)


    //テキストフィールドで編集する要素分の条件分岐を用意
    const handleChange = (event: any) => {
        if(editElementKey === 'cost'){
            if(payment.id !== null){

            } else {
                setNewPayment({ ...newPayment, cost: event?.target.value })
            }
        }
        if(editElementKey === 'name'){
            if(payment.id !== null){
                setPayments([
                    ...payments.slice(0, payment.id),
                    {...payment, name: event?.target.value},
                    ...payments.slice(payment.id+1)
                ])
            } else {
                setNewPayment({ ...newPayment, name: event?.target.value })
            }
        }
        if(editElementKey === 'category'){
            if(payment.id !== null){
                setPayments([
                    ...payments.slice(0, payment.id),
                    {...payment, category: event?.target.value},
                    ...payments.slice(payment.id+1)
                ])
            } else {
                setNewPayment({ ...newPayment, category: event?.target.value })
            }
        }
        if(editElementKey === 'date'){
            if(payment.id !== null){
                setPayments([
                    ...payments.slice(0, payment.id),
                    {...payment, date: event?.target.value},
                    ...payments.slice(payment.id+1)
                ])
            } else {
                setNewPayment({ ...newPayment, date: event?.target.value })
            }
        }
    }

    if(editElementKey === 'cost') {
        return <TextField value={payment.cost} onChange={handleChange} />
    } else if(editElementKey === 'name') {
        return <TextField value={payment.name} onChange={handleChange} />
    } else if(editElementKey === 'category') {
        return <TextField value={payment.category} onChange={handleChange} />
    } else if (editElementKey === 'date'){
        return <TextField type="date" value={payment.date} onChange={handleChange} />
    } else {
        return <></>
    }

}

export default ValueEditTextField
