import Button from '@mui/material/Button'
import { useNavigate } from 'react-router-dom'
import StyledButton from '../atoms/styledButton'

import ChoosePayedByButton from '../molecules/choosePayedByButton'
import EditDeleteButton from '../molecules/editDeleteButton'

import { useRecoilValue, useRecoilState } from 'recoil'
import { paymentState } from '../../recoil/atoms'

function HomeEffectPage() {
    const navigate = useNavigate()
    const buttonon = () => {
        navigate("/main")
    }

    const [payments, setPayments] = useRecoilState(paymentState)
    const handleClickCredit = (payment: any) => {
        setPayments([
            ...payments.slice(0, payment.id),
            {...payment, payedBy: 'credit'},
            ...payments.slice(payment.id+1)
        ])
    }
    const handleClickCash = (payment: any) => {
        setPayments([
            ...payments.slice(0, payment.id),
            {...payment, payedBy: 'cash'},
            ...payments.slice(payment.id+1)
        ])
    }


    return(
        <>
            <h1>かっこいいエフェクト</h1>
            <Button onClick={buttonon}>始める</Button>
            <StyledButton value={"スタイルドボタン"} style={{width: "100%"}}/>

            {payments.map((payment)=>(
                <ChoosePayedByButton
                    key={payment.id}
                    payent={payment}
                    handleClickCredit={handleClickCredit}
                    handleClickCash={handleClickCash}
                />
            ))   
            }

            <EditDeleteButton />
        </>
    )
}

export default HomeEffectPage
