import { TableCell, TextField } from '@mui/material'
import { useState } from 'react'

//変更するコンポーネント配列をインポート
import { useRecoilState } from 'recoil'
import { paymentState } from '../../recoil/atoms'

function ValueEditTextFieldCategory(props: any) {
    const payment = props.payment
    const [payments, setPayments] = useRecoilState(paymentState)

    const handleChange = (event: any) => {
        setPayments([
            ...payments.slice(0, payment.id),
            {...payment, category: event?.target.value},
            ...payments.slice(payment.id+1)
        ])
    }

    return <TextField value={payment.category} onChange={handleChange} />
}

export default ValueEditTextFieldCategory
