import { useRecoilValue } from 'recoil'
import { Grid, Paper } from '@mui/material'

//表示データ（お支払いデータ）・月、日ごと抜粋
import { paymentState } from '../../recoil/atoms'
import CrudUnitTable from '../organisms/crudUnitTable'

//スライドショー
import 'swiper/css'
import 'swiper/css/pagination'
import "swiper/css/navigation"
import { Swiper, SwiperSlide } from 'swiper/react'
import { Pagination, Navigation } from 'swiper'

//カレンダー（FullCalendar）
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid';

function CreatePaymentsTemplate () {
    const payments = useRecoilValue(paymentState)

    const monthlyDays = (year: number, month: number) => { 
        const dayNum = (month: number) => {
            if([1,3,5,7,8,10,12].some(monthHas31day => monthHas31day === month)) {
                return 31
            } else {
                return 30
            }
        }
        const days = [...Array(dayNum(month))].map((_, index: number) => {
            if (month < 10) {
                if (index < 9) { return year + "-0" + month + "-0" + (index+1) }
                if (index >= 9) { return year + "-0" + month + "-" + (index+1) }
            }
            if (month >= 10) {
                if (index < 9) { return year + "-" + month + "-0" + (index+1) }
                if (index >= 9) { return year + "-" + month + "-" + (index+1) }
            }
        })
        return days
    }

    //const events = [
    //    {title:'event1', start: '2022-07-14'},
    //    {title:'event2', start: '2022-07-15', end: '2022-07-16'}
    //]

    interface PaymentBar {
        title: string
        start: string
    }

    const paymentBars = [] as PaymentBar[] 

    monthlyDays(2022,7).map((day) => {

        const paymentsOfDay = payments.filter(payment => payment.date === day)

        paymentsOfDay.map((paymentOfDay) => {
            if(paymentOfDay !== null && paymentOfDay !== undefined) {
                paymentBars.push({
                    title: paymentOfDay.cost.toString(),
                    start: paymentOfDay.date
                })
            }
        })

    })

    console.log(paymentBars)

    return(
    <div>
        <h1>新規作成</h1>
        <Grid container spacing={2}>
            <Grid item xs={6}>
                <Paper style={{padding: "20px", margin: "10px"}}>
                    <FullCalendar
                        plugins={[dayGridPlugin, timeGridPlugin]}
                        initialView="dayGridMonth"
                        events={paymentBars}
                    />
                </Paper>
            </Grid>
            <Grid item xs={6}>

            </Grid>
        </Grid>
    </div>
    )
}

export default CreatePaymentsTemplate
