import { useRecoilValue } from 'recoil'
import { Grid, Paper } from '@mui/material'

//表示データ（お支払いデータ）
import { paymentState } from '../../recoil/atoms'
import CrudUnitTable from '../organisms/crudUnit/crudUnitTable'

//カレンダー（FullCalendar）
import PaymentCalender from '../organisms/paymentCalender'

//スライドショー
import 'swiper/css'
import 'swiper/css/pagination'
import "swiper/css/navigation"
import { Swiper, SwiperSlide } from 'swiper/react'
import { Pagination, Navigation } from 'swiper'

function CreatePaymentsTemplate () {
    const payments = useRecoilValue(paymentState)

    //choose payment by month
    const PaymentsByMonth = (year: number, month: number) => {
        const pickedPayments = payments.filter((payment) => 
            year === (new Date(payment.date)).getFullYear() && month === (new Date(payment.date)).getMonth()+1
        )
        return pickedPayments
    }

    return(
    <div>
        <h1>新規作成</h1>
        <Grid container spacing={2}>
            <Grid item xs={6}>
                <Paper style={{padding: "20px", margin: "10px"}}>
                    <PaymentCalender
                        year={2022}
                        month={7}
                    />
                </Paper>
            </Grid>
            <Grid item xs={6}>
                <Paper>
                    <CrudUnitTable
                        data={PaymentsByMonth(2022,7)}
                        columnNames={[ '金額', '名前', '項目', '支払い方法', '支払日', ]}
                    />
                </Paper>
            </Grid>
        </Grid>
    </div>
    )
}

export default CreatePaymentsTemplate
